import { Component, Input, OnInit } from '@angular/core';
import { FilterConfig, FilterItemConfig, FilterType } from '../filter.models';

@Component({
  selector: 'app-filter',
  templateUrl: './filter.component.html',
  styleUrls: ['./filter.component.css']
})
export class FilterComponent implements OnInit {
  @Input() config: FilterConfig;

  filters: Map<FilterType, FilterItem> = new Map();
  filter: any = {};

  ngOnInit(): void {
    this.config.filters.forEach((filter_config) => {
      this.filters.set(filter_config.type, new FilterItem(filter_config))
    })
    console.log(this.filters)
    this.update(`000`, this.filters.get(FilterType.Rede));
  }

  update(hash: string, filter: FilterItem) {
    this.filter[filter.config.key] = hash;
    this.notify();
  }

  notify() {
    this.filters.forEach((filter) => {
      console.log(filter.config);
    })
  }

}

class FilterItem {
  config: FilterItemConfig;

  constructor(config: FilterItemConfig) {
    this.config = config;
  }
}
<p>filter works!</p>

<ng-container *ngFor="let filter of filters | keyvalue">
	<!-- <span>{{filter.value.config.type}}</span> -->

	<select>
		<option *ngFor="let option of filter.value.config.update_list">
			{{option}}
		</option>
	</select>

</ng-container>
