    { hash: '000', descricao: 'XXX', },
    { hash: '000', descricao: 'XXX', },
    { hash: '000', descricao: 'XXX', },
    { hash: '000', descricao: 'XXX', },
  ]

  constructor(config: FilterItemConfig) {
    this.config = config;
  }
}
